jQuery(document).ready(function ($) {
    $.fn.datepicker.defaults = {
        allowDeselection: false,
    };

    $('#check_in_date').datepicker({
        language: 'en',
        format: "yyyy-mm-dd",
        startDate: "0d",
        forceParse: 'false',
        startView: 0,
        minViewMode: 0,
        todayBtn: false,
        autoclose: true,
        todayHighlight: true,
    }).on('changeDate', function (e) {
        var date = new Date($(this).datepicker('getDate'));
        console.log(date);

        date.setDate(date.getDate() + 1);
        date.setMonth(date.getMonth() + 1);

        var date_out = date.getFullYear() + '-' + date.getMonth()  + '-' + (date.getDate());
        $('#check_out_date').datepicker('setDate',date_out);
        $('#check_out_date').datepicker('setStartDate', date_out);
    });

    $('#check_out_date').datepicker({
        language: 'en',
        format: "yyyy-mm-dd",
        startDate: "+1",
        forceParse: 'false',
        startView: 0,
        minViewMode: 0,
        autoclose: true,
        todayHighlight: true
    });

    var date = new Date();
    date.setMonth(date.getMonth() + 1);
    var date_in = date.getFullYear()  + '-' + date.getMonth() + '-' + date.getDate();
    var date_out =  date.getFullYear() + '-' + date.getMonth() + '-' + (date.getDate() + 1 );

    $('#check_in_date').datepicker('setDate',date_in);
    $('#check_in_date').datepicker('setStartDate',date_in);

    $('#check_out_date').datepicker('setDate',date_out);
    $('#check_out_date').datepicker('setStartDate', date_out);
});
